import React from "react";

function ToDo({ id, task, completed, updateTask }) {
    const toDoStyle = {
        done: {
            height: 20,
            width: 20,
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },
        notDone: {

        }
    };

    return (
        <div>
            {completed ? <div style={toDoStyle.done}onClick={() => updateTask(id, 'state', false)}></div> : <div style={toDoStyle.notDone} onClick={() => updateTask(id, 'state', true)}></div>}
            <p>{task}</p>
        </div>
    );
};

export default ToDo;
