import './App.css';
import { useEffect, useState } from 'react';
import pageService from './services/Pages';
import Page from './components/Page';

/* crud
pages: (date, todo, events, entry, images)
 - create
 - read
 - update
 - destroy

todo:
 - create
 - read
 - update (whole page updates instead?)
 - destroy

events:
 - create
 - read
 - update (whole page updates instead?)
 - destroy

entry: (no create because it will automatically be created upon page creation, no delete because it's required)
 - read
 - update (whole page updates instead?)

images: (dont need update cause you should delete/create for new image, pages by default have "" for all image places)
 - create
 - read
 - destroy
*/

// new Date ex. Fri Mar 17 2023 16:59:43 GMT-0400 (Eastern Daylight Time)
// creates as object
// getDate() => 17
// getMonth() + 1 because its 0-index => 3

function App() {
  const [currentPage, setCurrentPage] = useState({});
  const [allPages, setAllPages] = useState([]);

  // function getAllPages() {
  //   pageService.read()
  //     .then(initialPages => {
  //       setAllPages(initialPages);
  //     });
  // };

  // // function getPage(id) {
  // //   pageService.one(id)
  // //     .then(returnedPage => {
  // //       console.log(returnedPage);
  // //       setCurrentPage(returnedPage);
  // //     });
  // // };

  // function getPage(id) {
  //   return allPages[id];
  // };

  function changePage(dir) {
    if (dir === 'back') {
      // getPage(currentPage.id - 1);
      const nextPageId = currentPage.id - 1;
      setCurrentPage(nextPageId);
    } else if (dir === 'forward') {
      // getPage(currentPage.id + 1);
      const nextPageId = currentPage.id + 1;
      setCurrentPage(nextPageId);
    }
  }

  // // onClick={() => {changePage('forward')}}

  useEffect(() => {
    pageService.read()
    .then(initialPages => {
      setAllPages(initialPages);
    });

    const startingPage = allPages.length - 1;
    setCurrentPage(allPages[startingPage]);
  }, []);

  return (
    <div className="App">
      <Page currentPage={currentPage} />

      <div>
        {currentPage.id >= allPages.length ? <p onClick={() => changePage('back')}>back</p> : null}
        <p>{currentPage.id}</p>
        {currentPage.id < allPages.length ? <p onClick={() => changePage('forward')}>forward</p> : null}
      </div>
    </div>
  );
}

export default App;
