import React, { useState } from "react";

function CalRow({ id, calArr, handleUpdate }) {
    const [hover, setHover] = useState(false);

    const calStyle = {
        div: {
            // display: 'inline',
        },

        normal: {
            margin: 5,
            cursor: 'pointer',
            fontSize: 15,
            display: 'inline'
        },

        hover: {
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },
    };

    function showEvent() {
        // console.log(calArr);
        return calArr.includes(id) ? calArr.id : 'nothing';
    };

    return (
        <div style={calStyle.div}>
            <p onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>
                {id % 3 === 0 || id === 0 ? id : '-'}
            </p>
            <button onClick={showEvent}>show event</button>
        </div>
    );
};

export default CalRow;
