import React from "react";

function Notif({ day, month, show }) {
    const notifStyle = {
        div: {
            position: 'absolute',
            backgroundColor: '#394a41',
            borderRadius: 10,
            marginLeft: 60,
            marginTop: 200,
            padding: 30,
            height: 140,
            width: 440,
            color: 'white',
            fontSize: 20,
            textAlign: 'center'
        },

        button: {
            backgroundColor: 'transparent',
            border: 'none'
        }
    };

    if (show === false) {
        return null
    };

    return (
        <div style={notifStyle}>
            <p>are you sure you want to delete the page for {month}.{day}?</p>
            <p>this action can't be undone.</p>

            <button>yes</button>
            <button>no</button>
        </div>
    );
};

export default Notif;
