import React, { useState, useEffect } from "react";

function CalRow({ id, calArr, handleUpdateEvent }) {
    const [hover, setHover] = useState(false);
    const [eventText, setEventText] = useState('');
    const [editingState, setEditingState] = useState(false);

    const calStyle = {
        normal: {
            cursor: 'pointer',
            fontSize: 15,
            display: 'flex',
            alignItems: 'center',
            border: '1px solid black'
        },

        hover: {
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },

        event: {
            marginLeft: 10,
            width: 200,
        },

        input: {
            marginLeft: 10,
            border: 'none',
            backgroundColor: 'transparent',
            fontSize: 15,
            width: 150,
            // minHeight: 15,
        }
    };

    function updateEvent() {
        console.log(eventText);
        setEditingState(false);
    };

    useEffect(() => {
        let obj;
        if (calArr) {
            obj = calArr.find(e => e.time === id);
        }

        if (obj) {
            setEventText(obj.event);
        };
    }, [calArr, id]);

    return (
        <div onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>
            <p>{id % 3 === 0 || id === 0 ? id : '-'}</p>

            {editingState 
                ? 
                <div>
                    <input />
                    <button onClick={updateEvent}>save</button>
                </div>
                : 
                <div style={calStyle.test} onClick={() => setEditingState(true)}>{eventText}</div>
            }
        </div>
    );
};

export default CalRow;

