import React from "react";

function ToDo({ id, task, completed, updateTask }) {
    const toDoStyle = {
        div: {
            userSelect: 'none',
        },

        done: {
            height: 20,
            width: 20,
            backgroundColor: 'rgba(219, 210, 195, 0.8)',
            borderRadius: 10,
            marginLeft: 10,
            display: 'inline-block',
            cursor: 'pointer',
            userSelect: 'none',
        },

        notDone: {
            height: 10,
            width: 10,
            borderRadius: 10,
            border: '5px solid rgba(219, 210, 195, 0.8)',
            marginLeft: 10,
            display: 'inline-block',
            cursor: 'pointer',
            userSelect: 'none',
        },

        p: {
            display: 'inline-block',
            marginLeft: 10,
            borderBottom: '2px solid rgba(219, 210, 195, 0.8)',
            width: 300,
            userSelect: 'none',
        }
    };

    return (
        <div style={toDoStyle.div}>
            {completed ? <div style={toDoStyle.done}onClick={() => updateTask(id, 'state', false)}></div> : <div style={toDoStyle.notDone} onClick={() => updateTask(id, 'state', true)}></div>}
            <p style={toDoStyle.p}>{task}</p>
        </div>
    );
};

export default ToDo;
