import React, { useState } from "react";

function ToDo({ id, task, completed, handleUpdate }) {
    const [editingState, setEditingState] = useState(false);

    const toDoStyle = {
        done: {
            height: 20,
            width: 20,
            backgroundColor: 'rgba(219, 210, 195, 0.8)',
            borderRadius: 10,
            marginLeft: 10,
            display: 'inline-block',
            cursor: 'pointer',
        },

        notDone: {
            height: 10,
            width: 10,
            borderRadius: 10,
            border: '5px solid rgba(219, 210, 195, 0.8)',
            marginLeft: 10,
            display: 'inline-block',
            cursor: 'pointer',
        },

        p: {
            display: 'inline-block',
            marginTop: 0,
            marginLeft: 10,
            borderBottom: '2px solid rgba(219, 210, 195, 0.8)',
            width: 250,
            fontSize: 17,
        }
    };

    function editText() {
        setEditingState(editingState => !editingState);
    };

    document.addEventListener('click', function(e) {
        const el = document.getElementById('current');
        console.log(el)
    })

    return (
        <div>
            {completed ? <div style={toDoStyle.done}onClick={() => handleUpdate(id, 'state', false)}></div> : <div style={toDoStyle.notDone} onClick={() => handleUpdate(id, 'state', true)}></div>}
            {/* <p style={toDoStyle.p}>{task}</p> */}
            {editingState ? <input id='current' type='text' /> : <p onClick={editText}style={toDoStyle.p}>{task}</p>}
        </div>
    );
};

export default ToDo;
