import React from "react";

function Notif({ day, month, show }) {
    const notifStyle = {
        position: 'absolute',
        backgroundColor: '#394a41',
        borderRadius: 10,
        marginLeft: 100,
        marginTop: 180,
        padding: 30,
        height: 140,
        width: 400,
        color: 'white',
        fontSize: 20,
        textAlign: 'center'
    };

    if (show === false) {
        return null
    };

    return (
        <div style={notifStyle}>
            <p>are you sure you want to delete the page for {month}.{day}?</p>
            <p>this action can't be undone.</p>

            <button>yes</button>
            <button>no</button>
        </div>
    );
};

export default Notif;
