import React, { useState, useEffect } from "react";

function CalRow({ id, calArr, handleUpdate }) {
    const [hover, setHover] = useState(false);
    const [eventText, setEventText] = useState('');
    const [editingState, setEditingState] = useState(false);

    const calStyle = {
        normal: {
            cursor: 'pointer',
            fontSize: 15,
            display: 'flex'
        },

        hover: {
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },

        event: {
            marginLeft: 10,
            width: 200,
        },

        input: {
            border: 'none',
            backgroundColor: 'transparent',
            fontSize: 15,
            width: 150,
        }, 

        button: {

        }
    };

    function updateEvent() {
        handleUpdate(id, 'event', eventText);
    };

    useEffect(() => {
        if (calArr) {
            const ev = calArr.map(e => e.time === id ? e.event : '');
            setEventText(ev[1]);
        };

        const input = document.getElementById('event-input');
        if (input) {
            input.focus();
        };
    }, [calArr, id, editingState]);

    return (
        <div onClick={updateEvent} onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>
            <p>{id % 3 === 0 || id === 0 ? id : '-'}</p>

            {/* <p style={{ ...calStyle.normal, ...calStyle.event }}>{eventText}</p> */}
            {editingState 
            ? <div>
                <input style={{...calStyle.event, ...calStyle.input}} id='event-input' value={eventText} onChange={(e) => setEventText(e.target.value)} /> 
                <button style={calStyle.button}>save</button>
              </div>
            : <p onClick={() => setEditingState(true)} style={{ ...calStyle.normal, ...calStyle.event }}>{eventText}</p>}
        </div>
    );
};

export default CalRow;
