import React, { useState } from "react";

function CalRow({ id, calArr, handleUpdate }) {
    const [hover, setHover] = useState(false);
    const [eventText, setEventText] = useState('');

    const calStyle = {
        normal: {
            // margin: 0,
            cursor: 'pointer',
            fontSize: 15,
            display: 'flex'
        },

        hover: {
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },

        event: {
            marginLeft: 10
        }
    };

    // function showEvent() {
    //     return calArr.map(e => e.time === id ? e.event : null);
    // };

    function updateEvent() {
        handleUpdate(id, 'event', 'test');
    };

    useEffect(() => {
        setEventText(calArr.map(e => e.time === id ? e.event : null));
    }, [calArr]);

    return (
        <div onClick={updateEvent} onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>
            <p>{id % 3 === 0 || id === 0 ? id : '-'}</p>

            {/* <p style={{ ...calStyle.normal, ...calStyle.event }}>{showEvent()}</p> */}
        </div>
    );
};

export default CalRow;
