import React, { useState } from "react";

function ToDo({ id, task, completed, handleUpdate }) {
    const [editingState, setEditingState] = useState(false);
    const [taskText, setTaskText] = useState(task);

    const toDoStyle = {
        done: {
            height: 20,
            width: 20,
            backgroundColor: 'rgba(219, 210, 195, 0.8)',
            borderRadius: 10,
            marginLeft: 10,
            display: 'inline-block',
            cursor: 'pointer',
        },

        notDone: {
            height: 10,
            width: 10,
            borderRadius: 10,
            border: '5px solid rgba(219, 210, 195, 0.8)',
            marginLeft: 10,
            display: 'inline-block',
            cursor: 'pointer',
        },

        p: {
            display: 'inline-block',
            marginTop: 0,
            marginLeft: 10,
            borderBottom: '2px solid rgba(219, 210, 195, 0.8)',
            width: 250,
            fontSize: 17,
        }
    };

    // document.querySelector('#myInput').addEventListener('keypress', function(e) {
    //     if (e.key === 'Enter') {
    //         console.log('enter button')
    //     }
    // })

    function handleTextChange(e) {
        setTaskText(e.target.value);

        // const input = document.getElementById('myInput');
        // input.addEventListener('keypress', function(e) {
        //     if (e.key === 'Enter') {
        //         console.log('enter')
        //     } 
        // })

        const input = document.querySelector('.myInput');
        input.addEventListener('keypress', function(e) {
            if (e.key === 'Enter' || e.keyCode === 13) {
                console.log('enter')
            }
        })
        // console.log(input)
    }

    return (
        <div>
            {completed ? <div style={toDoStyle.done}onClick={() => handleUpdate(id, 'state', false)}></div> : <div style={toDoStyle.notDone} onClick={() => handleUpdate(id, 'state', true)}></div>}
            {/* <p style={toDoStyle.p}>{task}</p> */}
            {editingState ? <input type='text' className='myInput' value={taskText} onChange={(e) => handleTextChange(e)} /> : <p onClick={() => setEditingState(editingState => !editingState)} style={toDoStyle.p}>{task}</p>}
        </div>
    );
};

export default ToDo;
