import React, { useState, useEffect } from "react";

function CalRow({ id, calArr, handleUpdateEvent }) {
    const [hover, setHover] = useState(false);
    const [eventText, setEventText] = useState('');

    const calStyle = {
        normal: {
            cursor: 'pointer',
            fontSize: 15,
            display: 'flex',
            alignItems: 'center'
        },

        hover: {
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },

        event: {
            marginLeft: 10,
            width: 200,
        },

        input: {
            marginLeft: 10,
            border: 'none',
            backgroundColor: 'transparent',
            fontSize: 15,
            width: 150,
        }, 

        button: {

        },

        placeholder: {
            color: 'transparent',
        }
    };

    function testFunc() {
        const test = calArr.find(e => e.time === id);
        console.log(test);
    };

    useEffect(() => {
        const obj = calArr.find(e => e.time === id);
        if (obj) {
            setEventText(obj.event);
            // console.log(obj);
        }
    }, []);

    return (
        <div onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>
            <p>{id % 3 === 0 || id === 0 ? id : '-'}</p>
            {/* <button onClick={testFunc}>test</button> */}

            <div>{eventText}</div>
        </div>
    );
};

export default CalRow;

