import React, { useState, useEffect } from "react";
import ToDo from "./ToDo";

function Page({ page, pageService }) {
    const [toDoArr, setToDoArr] = useState({});

    useEffect(() => {
        if (page) {
            setToDoArr(page.todos);
        }
    }, [page]);

    const pageStyle = {
        // border: '1px solid black'
    }
    const dateBoxStyle = {
        // color: 'green'
    };

    function mapToDos() {
        return toDoArr?.map?.(obj => <ToDo key={obj.id} id={obj.id} task={obj.task} completed={obj.completed} updateTask={updateTask} />);
    };

    // const toDos = toDoArr?.map?.(obj => <ToDo key={obj.id} id={obj.id} task={obj.task} completed={obj.completed} updateTask={updateTask} />);

    function updateTask(id, k, att) {
        const task = page.todos.find(t => t.id === id);

        if (k === 'task') {
          const changedTask = { ...task, task: att };
        } else if (k === 'state') {
          const changedTask = { ...task, completed: att };
      };
    };

    return (
        <div style={pageStyle}>
            <div style={dateBoxStyle}>
                <h2>{page.day}</h2>
                <h2>{page.month}</h2>
            </div>

            <p>{page.entry}</p>

            {/* {toDos ? toDos : null} */}
            {mapToDos()}
            {/* <button onClick={() => console.log(toDos)}>button</button> */}
        </div>
    )
};

export default Page;
