import './App.css';
import { useEffect, useState } from 'react';
import pageService from './services/Pages';
import Page from './components/Page';

// new Date ex. Fri Mar 17 2023 16:59:43 GMT-0400 (Eastern Daylight Time)
// creates as object
// getDate() => 17
// getMonth() + 1 because its 0-index => 3

function App() {
  const [currentPage, setCurrentPage] = useState({});
  const [pageCount, setPageCount] = useState(0);

  const appStyle = {
    // backgroundColor: 'red'
  };

  useEffect(() => {
    pageService.pageCount()
      .then(initialCount => {
        setPageCount(initialCount);
        getPage(initialCount);
      }); 
  }, []);

  function getPage(id) {
    pageService.getOne(id)
      .then(initialPage => {
        setCurrentPage(initialPage);
      });
  };

  function changePage(direction) {
    if (direction === 'forward') {
      const nextPageId = currentPage.id + 1;
      getPage(nextPageId);
    } else if (direction === 'back') {
      const nextPageId = currentPage.id - 1;
      getPage(nextPageId);
    };
  };

  function updateTask(id, k, att) {
    const task = currentPage.todos.find(t => t.id === id);

    if (k === 'task') {
      const changedTask = { ...task, task: att };
    } else if (k === 'state') {
      const changedTask = { ...task, completed: !task.completed }
    };

    // pageService.update(changedNote)
    //   .then(res => )
  };

  return (
    <div style={appStyle}>
      {/* <button onClick={() => console.log(currentPage.id)}>console.log current page</button> */}
      {currentPage ? <Page page={currentPage} updateTask={updateTask} /> : null}

      <div>
        {currentPage.id > 1 ? <p onClick={() => changePage('back')}>back</p> : null}
        <p>{currentPage.id}</p>
        {currentPage.id < pageCount ? <p onClick={() => changePage('forward')}>forward</p> : null}
      </div>
    </div>
  );
};

export default App;
