import React, { useState } from "react";

function CalRow({ id, calArr, handleUpdate }) {
    const [hover, setHover] = useState(false);

    const calStyle = {
        normal: {
            cursor: 'pointer',
            fontSize: 15,
            // display: 'inline-block'
        },

        hover: {
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },
    };

    function showEvent() {
        return calArr.map(e => e.time === id ? e.event : null);
    };

    return (
        <div onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>
            <p>{id % 3 === 0 || id === 0 ? id : '-'}</p>

            <p style={calStyle.normal}>{showEvent()}</p>
        </div>
    );
};

export default CalRow;
