import React, { useState } from "react";

function CalRow({ id, calArr, handleUpdate }) {
    const [hover, setHover] = useState(false);

    const calStyle = {

        normal: {
            margin: 5,
            cursor: 'pointer',
        },

        hover: {
            backgroundColor: 'rgba(219, 210, 195, 0.5)',
        },
    }
    return (
        <div>
            {id % 3 === 0 || id === 0 
                ? <p onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>{id}</p> 
                : <p onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)} style={{ ...calStyle.normal, ...(hover ? calStyle.hover : null)}}>-</p>}

            <p>{id % 3 === 0 || id === 0 ? id : null}</p>
        </div>
    );
};

export default CalRow;
